import { useDispatch, useSelector } from "react-redux";
import { useHistory, useLocation } from "react-router-dom";

import { useState } from "react";
import { userUpdated } from "./usersSlice";

export function EditUser() {
  const { pathname } = useLocation();
  const employeeId = parseInt(pathname.replace("/edit-user/", ""));

  const user = useSelector((state) =>
    state.users.entities.find((user) => user.employeeId === employeeId)
  );

  const dispatch = useDispatch();
  const history = useHistory();

  const [firstName, setFirstName] = useState(user.firstName);
  const [lastName, setLastName] = useState(user.lastName);
  const [birthday, setBirthday] = useState(user.birthday);
  const [height, setHeight] = useState(user.height);
  const [error, setError] = useState(null);

  const handleFirstName = (e) => setFirstName(e.target.value);
  const handleLastName = (e) => setLastName(e.target.value);
  const handleBirthday = (e) => setBirthday(e.target.value);
  const handleHeight = (e) => setHeight(e.target.value);

  const handleClick = () => {
    if (firstName && lastName && birthday && height) {
      dispatch(
        userUpdated({
          id: employeeId,
          firstName,
          lastName,
          birthday,
          height,
        })
      );

      setError(null);
      history.push("/");
    } else {
      setError("Нужно заполнить все поля");
    }
  };

  return (
    <div className="container">
      <div className="row">
        <h1>Редактировать данные</h1>
      </div>
      <div className="row">
        <div className="three columns">
          <label htmlFor="nameInput">Имя</label>
          <input
            className="u-full-width"
            type="text"
            placeholder="Имя"
            id="nameInput"
            onChange={handleFirstName}
            value={name}
          />
          <label htmlFor="emailInput">Email</label>
          <input
            className="u-full-width"
            type="email"
            placeholder="test@mailbox.com"
            id="emailInput"
            onChange={handleEmail}
            value={email}
          />
          {error && error}
          <button onClick={handleClick} className="button-primary">
            Сохранить
          </button>
        </div>
      </div>
    </div>
  );
}
